import React, { useState } from 'react';
import Card from '../UI/Card';
import  classes from './AddUser.module.css';
import Button from '../UI/Button';
import ErrorModel from '../UI/ErrorModel';
const AddUser = props => {
    const [username , setUsername] = useState('')
    const [age ,setAge] = useState('');
    const addUserHandler =(event)=>{
        event.preventDefault();
        if(username.trim().length === 0 || age.trim().length === 0 || age <= 0){
            console.log('Please enter a username and age')
            return;
        }
        props.onAddUser(username,age)
        setUsername('');
        setAge('');
        console.log(username, age)
    }
    const usernameChangedHandler =(event)=>{
        setUsername(event.target.value);
    }
    const ageChangedHandler =(event)=>{
        setAge(event.target.value);
    }
    return (
        <div className="backdrop">
        <ErrorModel title="An Error Occurred ! " message="Something Went Wrong !"/>
        <Card className={classes.input}>
        <form onSubmit={addUserHandler}>
            <label htmlFor='username'>Username</label>
            <input id='username' type='text' value={username} onChange={usernameChangedHandler}/>
            <label htmlFor='age'>Age (Years)</label>
            <input id='age' type='number' value={age} onChange={ageChangedHandler}/>
            <Button type='submit'>AddUser</Button>
        </form>
        </Card>
        </div>
        )
        
}

export default AddUser ;
